  /* Media query - dostosowuje wielkość tekstu na mniejszych ekranach */
  @media (max-width: 600px) {
    .test-heading {
      font-size: 6vw; /* Inna wartość na mniejszych ekranach */
    }
  }
  
  /* Dodaj media query z maksymalnym i minimalnym font-size */
  @media (min-width: 601px) and (max-width: 1024px) {
